import { Injectable } from '@angular/core';
declare var jsPDF: any; // Important

@Injectable()
export class Ng2PdfService {
    private styles = {
      lineColor: 200,
      lineWidth: 0,
      fontStyle: 'normal', // normal, bold, italic, bolditalic
      halign: 'left', // left, center, right
      valign: 'middle', // top, middle, bottom
      columnWidth: 'auto', // 'auto', 'wrap' or a number
      overflow: 'linebreak',
      fontSize: 7,
      font: 'helvetica'
    }

    // tslint:disable-next-line:max-line-length
    private img = 'data:image/png;base64,';
    constructor( ) {

     }

    // Only pt supported (not mm or in)
    pdfTableDate( columns: string[], rows: any[], titleTable = 'Table', fileName = 'File.pdf', date) {
        const doc = new jsPDF('p', 'pt', 'a4');
        doc.autoTable(columns, rows, {
            theme: 'striped',
            margin: { top: 100 },
            styles: this.styles,
            headerStyles: {
              fillColor: [51, 122, 183],
              textColor: [255],
              halign: 'center'
            },
            addPageContent: data => {
                doc.addImage(this.img, 'PNG', 410, 10, 140, 60);
                doc.setFontSize(12);
                doc.text(titleTable, 40, 30);
                doc.setFontSize(10);
                doc.text(date, 429, 80);
            }
        });
        doc.save(fileName);
    }

    pdfTableDateAccountStatus( columns: string[], rows: any[], titleTable = 'Table', fileName = 'File.pdf', date) {
        const doc = new jsPDF('p', 'pt', 'a4');
        doc.autoTable(columns, rows, {
            theme: 'striped',
            margin: { top: 150 },
            styles: this.styles,
            headerStyles: {
              fillColor: [51, 122, 183],
              textColor: [255],
              halign: 'center'
            },
            addPageContent: data => {
                doc.addImage(this.img, 'PNG', 410, 10, 140, 60);
                doc.setFontSize(12);
                doc.text(titleTable, 40, 30);
                doc.setFontSize(10);
                doc.text(date, 429, 80);
            }
        });
        doc.save(fileName);
    }

    // tslint:disable-next-line:max-line-length
    pdfTableWithDates( columns: string[], rows: any[], titleTable = 'Table', fromDate, toDate, name, saleType = '', fileName = 'File.pdf', center = false, canvas = null) {

      let customStyles = {};

      if (center) {
        Object.assign(customStyles, this.styles)['halign'] = 'center';
      }

      const doc = new jsPDF('p', 'pt', 'a4');
      const date = `| FECHA: ${new Date().toLocaleDateString()} |`;
      doc.autoTable(columns, rows, {
        margin: { top: 100 },
        styles: center ? customStyles : this.styles,
        headerStyles: {
          fillColor: [51, 122, 183],
          textColor: [255],
          halign: 'center'
        },
        theme: 'striped',
        addPageContent: data => {
          doc.addImage(this.img, 'PNG', 410, 10, 140, 60);
          doc.setFontSize(12);
          doc.text(titleTable, 40, 30);
          doc.setFontSize(10);
          doc.text(name, 40, 60);
          doc.text(fromDate, 40, 80);
          doc.text(toDate, 150, 80);
          doc.text(saleType, 270, 80);
          doc.text(date, 429, 80);
          if(canvas) {
            doc.addPage();
            doc.text(`GRÁFICA: ${name}`, 190, 30);
            doc.addImage(canvas.toDataURL(), 'PNG', 140, 40, canvas.width / 1.5, canvas.height / 1.5);
          }
          
        }
      });
      doc.save(fileName);
  }
}
